    { id: 1, text: "Doctors appointment", day: "February 1, 10:20 AM", reminder: true },
    { id: 2, text: "Food appointment", day: "November 1, 9:10 AM", reminder: false },
    { id: 3, text: "Eye appointment", day: "March 1, 15:20 PM", reminder: true },
    { id: 4, text: "Gym appointment", day: "April 1, 16:20 PM", reminder: false }
  ])

