import React from 'react';
import Button from './Button';

const Header = ({title}) => {
    const myClick = () =>{
        console.log("CLICK FROM HEADER")
    }
    return (
        <header className='header'>
            <h1>{title}</h1>
            <Button color = 'green' text = 'Add' myClick = {myClick}/>
        </header>
    );
};

//To handle default props passed in
Header.defaultProps = {
    title: 'DEFAULT SHIT',
}


export default Header;

