import React from 'react';
const tasks = [
    {id: 1, text: "DR appoint", day:"February 1", reminder:true},
    {id: 2, text: "Food appoint", day:"November 1", reminder:false},
    {id: 3, text: "Eye appoint", day:"March 1", reminder:true},
    {id: 4, text: "Gym appoint", day:"April 1", reminder:false}
]

//create a list using map for the data
const Tasks = () => {
  return (
  <>
  {tasks.map((task) => (<h3>{task.text, task.id}</h3>) )}
  </>
  );
};

export default Tasks;
